package com.example.shoppingcart.repositories;

import androidx.lifecycle.LiveData;
import androidx.lifecycle.MutableLiveData;

import com.example.shoppingcart.models.Product;

import java.util.ArrayList;
import java.util.List;
import java.util.UUID;

public class ShopRepo {

    private MutableLiveData<List<Product>> mutableProductList;

    public LiveData<List<Product>> getProducts() {
        if (mutableProductList == null) {
            mutableProductList = new MutableLiveData<>();
            loadProducts();
        }
        return mutableProductList;
    }

    private void loadProducts() {
        List<Product> productList = new ArrayList<>();
        productList.add(new Product(UUID.randomUUID().toString(), "aloo paratha", 40, true, "https://www.whiskaffair.com/wp-content/uploads/2019/06/Aloo-Paratha-1-3-500x500.jpg" ));
        productList.add(new Product(UUID.randomUUID().toString(), "Veg Burger", 50, true, "https://i2-prod.manchestereveningnews.co.uk/incoming/article13066616.ece/ALTERNATES/s615/pri_39204164.jpg"));
        productList.add(new Product(UUID.randomUUID().toString(), "Veg sandwich", 25, true, "https://mk0tw0mdal2noq6fa.kinstacdn.com/wp-content/uploads/sites/4/2016/01/04-2.jpg"));
        productList.add(new Product(UUID.randomUUID().toString(), "pani puri", 35, true, "https://www.patnadaily.com/images/stories/recipes/panipuri.jpg"));
       // productList.//add(new Product(UUID.randomUUID().toString(), "iPhone 11 Pro", 999, true, ""));
        productList.add(new Product(UUID.randomUUID().toString(), "Oreo Cake", 500, true, "https://www.bunsenburnerbakery.com/wp-content/uploads/2011/11/chocolate-oreo-layer-cake-square-9Q2B9289-735x735.jpg"));
        productList.add(new Product(UUID.randomUUID().toString(), "snickers shake", 65, true, "https://i0.wp.com/www.mommytravels.net/wp-content/uploads/2016/06/snickers-milkshake-.jpg?resize=720%2C720&ssl=1"));
        productList.add(new Product(UUID.randomUUID().toString(), "Momos", 90, true, "https://i.pinimg.com/originals/c6/45/31/c64531311222b2e387e1d72e8f0825b4.jpg"));
        productList.add(new Product(UUID.randomUUID().toString(), "samosa", 25, false, "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcTab7BHnpn7J1xTw6CiaTLZtutthpjYlB4rZA&usqp=CAU"));
        productList.add(new Product(UUID.randomUUID().toString(), "Chicken Nuggets", 50, true, "https://static.toiimg.com/thumb/53222328.cms?width=1200&height=900"));
        mutableProductList.setValue(productList);
    }
}
